#include <stdio.h>
#include <stdlib.h>
#include <math.h>
#include <string.h>
#define lli long long int
#define f1(i,bot,top) for (lli i=bot; i<top; i++)
#define f2(i,top,bot) for (lli i=top-1; i>=bot; i--)
#define el else
#define ei else if
#define INF 2147483647

int base = 8;
char enc_mes;
char dec_mes[2000];

void Encrypt()
{
    int ID_ascii = enc_mes;
    char ans[]="00000000";
    ans[8]='\0';
    for(int i=7; i>=0; i--)
    {
        if(ID_ascii%2==1) ans[i] = '1';
        ID_ascii /= 2;
    }
    printf("%s", ans);
}

int BinaryToAscii(int ID_start)
{
    int sum = 0;
    int ctr = 7;
    int ID_end = ID_start+7;
    for(int i=ID_start; i<=ID_end; i++)
    {
        if(dec_mes[i] == '1') sum+= (int)pow(2,ctr);
        ctr--;
    }
    return sum;
}

void Decrypt()
{
    int len = strlen(dec_mes);
    for(int i=0; i<len; i+=8)
    {
        int toPrint = BinaryToAscii(i);
        printf("%c",toPrint);
    }
}

int main()
{
    int c;
    scanf("%d\n", &c);
    
    if(c == 1)
    {
        while (scanf("%c", &enc_mes) != EOF) Encrypt();
    }
    if(c == -1)
    {
        while (scanf("%s", dec_mes) != EOF) Decrypt();
    }
}

